mapper.select:
SELECT dept_id
FROM sys_role_dept
WHERE tenant_id = #{tenantId}
  AND role_id = #{roleId}

SELECT DISTINCT rd.dept_id FROM sys_role_dept rd INNER JOIN sys_role r ON rd.role_id = r.id AND rd.tenant_id = r.tenant_id WHERE rd.tenant_id = #{tenantId} AND r.code IN
#{roleCode}
AND r.is_deleted = 0
